    } catch (err) {
      console.error('Erreur arr√™t scanner:', err);
    }
  };

  /**
   * Callback succ√®s scan QR - AVEC PROTECTION ANTI-DOUBLON
   */
  const onScanSuccess = async (decodedText: string) => {
    // Protection contre les scans multiples
    if (isProcessingRef.current) {
      console.log('Scan ignor√© - traitement en cours');
      return;
    }

    // Bloquer imm√©diatement les nouveaux scans
    isProcessingRef.current = true;
    console.log('QR Code scann√©:', decodedText);

    // Arr√™ter le scanner IMM√âDIATEMENT
    try {
      if (scannerRef.current) {
        await scannerRef.current.stop();
        scannerRef.current = null;
        setIsScanning(false);
      }
    } catch (e) {
      console.error('Erreur arr√™t scanner:', e);
    }

    try {
      // üîÑ NOUVEAU: Passer directement le QR data brut
      // La fonction validate_scanned_badge d√©tecte automatiquement le type:
      // - Badge statique: code simple (ex: "F29F85-81739C")
      // - Badge dynamique: JWT complet (ex: "eyJhbGciOi...")
      // - Badge JSON: parse et extrait le token/badge_code

      let qrData = decodedText;

      // Si c'est du JSON, extraire le token ou badge_code
      try {
        const parsed = JSON.parse(decodedText);
        qrData = parsed.token || parsed.badge_code || parsed.qr_data || decodedText;
        console.log('üìã QR JSON pars√©, extracted:', qrData.substring(0, 30) + '...');
      } catch {
        // Pas du JSON, c'est un code brut (statique ou JWT direct)
        console.log('üìã QR brut (non-JSON):', qrData.substring(0, 30) + '...');
      }

      // Valider le badge (supporte statique ET dynamique)
      await validateAndRecordScan(qrData);

      // Succ√®s: recharger les stats depuis la DB
      await loadScanStats();

    } catch (err: any) {
      console.error('Erreur traitement scan:', err);
      toast.error('Badge invalide', {
        description: err.message || 'Impossible de valider ce badge'
      });
    } finally {
      // D√©bloquer apr√®s 1 seconde pour √©viter les doubles scans accidentels
      setTimeout(() => {
        isProcessingRef.current = false;
      }, 1000);
    }
  };

